const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 2, description: "Charger", quantity: 12, packed: false },
];

export default function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1>ü´õFar Away</h1>;
}

function Form() {
  return (
    <div className="add-form">
      <h3>What do you need for your trip??</h3>
    </div>
  );
}

///////////////////////

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item item={item} />
        ))}
      </ul>
    </div>
  );
}

function Item({ item }) {
  return (
    <li>
      <span>
        {item.quantity} {item.description}
      </span>

      <button>‚ùå</button>
    </li>
  );
}

////////////////////////

/// Parent is a Ul
/// we map over the array, and for each object, we render an item component with an item prop inside
/// the item is is each object
/// this component is the one we are rendering on the page

/// the child is the li
/// the actual compoent we are rendering for each of the objects
/// the item.quantiy and item description

function Stats() {
  return (
    <footer className="stats">
      <em>You have X items on your list, and you already packed X (x%)</em>
    </footer>
  );
}
